import React from "react";
import html2canvas from "html2canvas";
import "./DownloadButton.css";

const DownloadButton = () => {
  const captureImage = (action) => {
    const frame = document.querySelector(".photo-frame-container");

    if (frame) {
      const elementWidth = 377.95275591;
      const elementHeight = 559.37;
      html2canvas(frame, {
        scale: 4,
        useCORS: true,
        backgroundColor: null,
        windowWidth: elementWidth,
        windowHeight: elementHeight,
      }).then((canvas) => {
        if (action === "print") {
          const imgData = canvas.toDataURL("image/png");
          const printWindow = window.open("", "", "height=600,width=800");
          printWindow.document.write(
            `<img width="100%" height="100%" src="${imgData}">`
          );
          printWindow.document.close();
          printWindow.focus();
          printWindow.print();
          printWindow.close();
        } else if (action === "download") {
          const link = document.createElement("a");
          link.href = canvas.toDataURL("image/png");
          link.download = "insaengnecut.png";
          link.click();
        }
      });
    } else {
      alert("사진이 준비되지 않았습니다.");
    }
  };

  return (
    <div className="download-box">
      <button className="print-button" onClick={() => captureImage("print")}>
        출력
      </button>
      <button
        className="download-button"
        onClick={() => captureImage("download")}
      >
        다운로드
      </button>
    </div>
  );
};

export default DownloadButton;
